export function wrapStorage(storage,initialData={}){const publicAPI={get size(){return storage.length},keys(){return Array.from({length:this.size},(_,i)=>storage.key(i))},get(key){const item=storage.getItem(key);return null===item?void 0:JSON.parse(item)},getAll(){return Object.fromEntries(this.keys().map(key=>[key,this.get(key)]))},set(key,value){storage.setItem(key,JSON.stringify(value))},remove:storage.removeItem.bind(storage),clear:storage.clear.bind(storage)};for(const[key,value]of Object.entries(initialData))null===storage.getItem(key)&&publicAPI.set(key,value);return publicAPI}export function addTeamName(name){storage.set("teamName",name)}